import isUrl from "is-url"

/**
 * shows time interval b/w two pictures
 */
let timer

let deleteFirstPhotoDelay

async function start() {
    try {
        const response = await fetch("https://dog.ceo/api/breeds/list/all")
        const data = await response.json()
        createBreedList(data.message)
    } catch (e) {
        console.log('There was a problem fetching the breed list.')
    }
}

function createBreedList(breedList) {
    document.getElementById("breed").innerHTML = `
    <select onchange="loadByBreed(this.value)">
        <option>Choose a dog breed</option>
        ${Object.keys(breedList).map(breed => {
            return `<option>${breed}</option>`
        }).join('')}
    </select>
    `
}

async function loadByBreed(breed) {
    if (breed !== 'Choose a dog breed') {
        const response = await fetch(`https://dog.ceo/api/breed/${breed}/images`) // fetch images for a concrete breed
        const data = await response.json()
        createSlideshow(data.message)
    }
}

function createSlideshow(images) {
    let currentPosition = 0
    clearInterval(timer)
    clearTimeout(deleteFirstPhotoDelay)

    if (images.length > 1) {
        document.getElementById('slideshow').innerHTML = `
        <div class="slide" style="background-image: url('${images[0]}')"></div>
        <div class="slide" style="background-image: url('${images[1]}')"></div>
        `
        currentPosition += 2
        if (images.length === 2) {
            currentPosition = 0
        }
        timer = setInterval(nextSlide, 3000) // slides are changed every 3 sec
    } else { // keep showing a single image
        document.getElementById('slideshow').innerHTML = `
        <div class="slide" style="background-image: url('${images[0]}')"></div>
        <div class="slide"></div>
        `
    }

    function nextSlide() {
        document.getElementById('slideshow').insertAdjacentHTML("beforeend", `<div class="slide" style="background-image: url('${images[currentPosition]}')"></div>`)
        deleteFirstPhotoDelay = setTimeout(() => {
            document.querySelector('.slide').remove()
        }, 1000)

        if (currentPosition + 1 >= images.length) { // if we reached the last picture in a breed list
            currentPosition = 0
        } else {
            currentPosition++
        }
    }
}

